package com.xworkz.Arrays;

public class ArrayCreation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//type 1-array  declaration and initialization
		int array[]= {11,12,13,14,15};
		for(int i=0;i<array.length;i++)
		{
			System.out.println("array index:"+i+" "+"element is:"+array[i]);
		}
		System.out.println("  ");
		
		//type 2-array  declaration and initialization
		int[] array1= {12,34,5,66,77};
		for(int i=0;i<array1.length;i++)
		{
			System.out.println("array index:"+i+" "+"element is:"+array1[i]);
		}
		

	}

}
